import styled from "styled-components";


export const StyledCardWrapper = styled.div`
    border: 0.0625rem solid red;
    border-radius: 20px;
    box-shadow: 0.1875rem 0.125rem 0.625rem rgba(0,0,0,0.1);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    background:crimson;
    overflow:hidden; 
`;

export const StyledText = styled.p`
    color: white;
    width:100%;
    height:60px;
    font-size: 1.3rem;
    font-weight:bold;
`;


export const StyledImage = styled.img`
    width:100%;
    height:400px;
`;

export const Styledowerview = styled.p`
    color: white;
    width:90%;
    height:auto;
    font-size: 1em;
    text-align:justify;
    padding:10px;
    display: none;
`;
