import React, { useState } from "react";
import { RelativdivTitle, Show, Relativdiv, StyledCardWrapper, StyledText, StyledImage, Styledowerview } from "./Card.style"

export const Card = (props) => {
    const [showData, setShowData] = useState('none');
    function showOverview() {
        if (showData == 'none') {
            setShowData('block')
        } else {
            setShowData('none')
        }
    };



    return (
        <div>
            <StyledCardWrapper>
                <div>
                    <Relativdiv>
                        <Show onClick={showOverview}>
                            <StyledImage src={props.poster_path} />
                        </Show>
                        <Styledowerview style={{ display: showData }}>
                            {props.overview}
                        </Styledowerview>
                    </Relativdiv>
                    <RelativdivTitle>

                        <StyledText>
                            {props.title}
                        </StyledText>
                    </RelativdivTitle>

                </div>
            </StyledCardWrapper>
        </div>
    )
}

