import React, { useState, useEffect } from "react";
import { Card } from "../Card";
import axios from "axios";
import { CardListWrapper } from "./CardList.style"
import { VideoSection } from '../VideoSection';
import { SearchBar } from '../SearchBar';

const apiKey = "d161df73b66b9d1b527d3526c891aeca"; //temporary
const baseUrl = "https://api.themoviedb.org/3/search/movie";

export const CardList = (props) => {
    const [movieData, setMovieData] = useState(null);
    const [inputData, setInputData] = useState('')

    useEffect(() => {
        axios.get(baseUrl, {
            params: {
                api_key: apiKey,
                query: 'Star'
            }
        })
            .then((res) => setMovieData(res.data.results))
            .catch((err) => console.log(err))
    }, [])

    const changeSearchValue = (input) => {
            setInputData(input);
            console.log(inputData);
        }
    return (
        <>
            <SearchBar placeholder='Search your movie...' onChange={(value) => changeSearchValue(value)} />
            <VideoSection/>
            <CardListWrapper>
                {movieData && movieData.map(movie => {
                    return <Card title={movie.title} release_date={movie.release_date} poster_path={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} />

                    })

                }
            </CardListWrapper>
        </>
    )
}
