import React, { useState, useEffect } from "react";
import axios from "axios";
import {Pagination} from "@material-ui/lab";

// Components
import { Card } from "./components/Card";
import { VideoSection } from './components/VideoSection';
import { SearchBar } from './components/SearchBar';

// Style Component
import { CardWrapper } from './App.style'

const apiKey = "d161df73b66b9d1b527d3526c891aeca"; //temporary
const baseUrl = "https://api.themoviedb.org/3/search/movie";

function App(props) {
  const [movieData, setMovieData] = useState(null);
  const [inputData, setInputData] = useState('live')
  const [page, setPage] = useState(1);
  const [count, setCount] = useState(0);


  const getRequestParams = (inputData, page, apiKey) => {
    let params = {};

    if (inputData) {
      params["query"] = inputData;
    }

    if (page) {
      params["page"] = page - 1;
    }

    if (api_key) {
      params["api_key"] = apiKey;
    }

    return params;
  };


  const handlePageChange = (event, value) => {
    setPage(value);
  };

  useEffect(() => {
  ;
  axios.get(baseUrl,getRequestParams(inputData, page, apiKey) )
      .then((res) => {
        setMovieData(res.data.results);
        setCount(length(res.data.results));})
  }, [inputData, page])

  function ChangeSetInputData(e) {
    setInputData(e.target.value)
  }

  return (
    <>
      <VideoSection />
      <SearchBar onChange={ChangeSetInputData} />
      <Pagination
            className="my-3"
            count={count}
            page={page}
            siblingCount={1}
            boundaryCount={1}
            variant="outlined"
            shape="rounded"
            onChange={handlePageChange}
          />
        <CardWrapper>
          {movieData?.map(
            (movie, index) => {
              return <Card
                key={index}
                title={movie.title}
                poster_path={
                  movie.poster_path ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
                    : "https://tse2.mm.bing.net/th?id=OIP.ai9mtrwlPjK4Tutfq4m-ugHaL2&pid=Api"}
                overview={movie.overview}
                release_date={movie.release_date}
                vote_average={movie.vote_average} />
            })
          }
        </CardWrapper>

    </>

  );
}

export default App;
