import React, { useState, useEffect } from "react";
import { Card } from "../Card";
import axios from "axios";
import { CardListWrapper } from "./CardList.style"
import { VideoSection } from '../VideoSection';
import { SearchBar } from '../SearchBar';

const apiKey = "d161df73b66b9d1b527d3526c891aeca"; //temporary
const baseUrl = "https://api.themoviedb.org/3/search/movie";

export const CardList = (props) => {
    const [movieData, setMovieData] = useState(null);
    const [inputData, setInputData] = useState('live')

    useEffect(() => {
        axios.get(baseUrl, {
            params: {
                api_key: apiKey,
                query: inputData,
                page:2,
            }
        })
            .then((res) => setMovieData(res.data.results[0]))
    }, [inputData])


    return (
        <>
            <SearchBar onChange={e => setInputData(e.target.value)} />
            <VideoSection />
            <CardListWrapper>

                    <Card title={movieData.title} release_date={movieData.release_date} poster_path={`https://image.tmdb.org/t/p/w500${movieData.poster_path}`} overview={overview} />

            </CardListWrapper>
        </>
    )
}
